    public void testCompSci(){
        AreasOfStudy areasOfStudy = new AreasOfStudy();
       
        
       ArrayList<String> classesTaken=new ArrayList<String>();
       classesTaken.add("COMP 123");
       classesTaken.add("COMP 127");
       classesTaken.add("COMP 128");
       classesTaken.add("MATH 279");
       classesTaken.add("STAT 155");
        DegreeComparator comparison=new DegreeComparator(classesTaken);
        assertEquals(2,comparison.numCoursesLeftForClosestStudy_ForTest);
    }


    
